#include <windows.h>\n#include <shlobj.h>\n#include <iostream>\n#include <string>\n\n#pragma comment(lib, "shell32.lib")\n\nstd::wstring GetRecycleBinPathForDrive(const std::wstring& driveLetter) {\n    if (driveLetter.empty() || driveLetter.size() > 1) return L"";\n\n    std::wstring path = driveLetter + L":\\$Recycle.Bin";\n    DWORD attributes = GetFileAttributes(path.c_str());\n\n    if ((attributes != INVALID_FILE_ATTRIBUTES) &&\n        ((attributes & FILE_ATTRIBUTE_DIRECTORY))) {\n            return path;\n    }\n\n    return L"";\n}\n\nint main() {\n    std::wstring recycleBinPath = GetRecycleBinPathForDrive(L"C");\n    if (!recycleBinPath.empty()) {\n        wprintf(L"The Recycle Bin path on C: is %s\n", recycleBinPath.c_str());\n    } else {\n        wprintf(L"Could not find the Recycle Bin for drive C:\n");\n    }\n    return 0;\n}