#include <iostream>\n   #include <uuid/uuid.h>\n\n   int main() {\n       uuid_t uuid;\n       char uuid_str[37];\n\n       // Generate a unique UUID\n       uuid_generate(uuid);\n\n       // Convert UUID to string format\n       uuid_unparse(uuid, uuid_str);\n\n       // Output the UUID as a string\n       std::cout << "Generated UUID: " << uuid_str << std::endl;\n\n       return 0;\n   }