// Example of a simple WT application setup\n#include <Wt/WApplication.h>\n#include <Wt/WContainerWidget.h>\n#include <Wt/WLabel.h>\n\nclass MyApp : public Wt::WApplication {\npublic:\n    MyApp(const Wt::WEnvironment& env)\n        : Wt::WApplication(env) {\n        setTitle("WT Example");\n        \n        // Create a container widget\n        auto *container = root()->addWidget(std::make_unique<Wt::WContainerWidget>());\n        \n        // Add a label to the container widget\n        container->addWidget(std::make_unique<Wt::WLabel>("Hello, world"));\n    }\n};\n\nint main(int argc, char **argv) {\n    return WRun(argc, argv, [](const Wt::WEnvironment& env) {\n        return std::make_unique<MyApp>(env);\n    });\n}