#include <iostream>\n#include <cmath>\n\ndouble unixToJulian(long unixTime) {\n    // Constants for conversion\n    const double SECONDS_IN_DAY = 86400.0;\n    const double JD_UNIX_EPOCH = 2440587.5; // Julian Date at Unix Epoch (1970-01-01T00:00:00Z)\n\n    // Convert the Unix timestamp to Julian Date\n    return JD_UNIX_EPOCH + unixTime / SECONDS_IN_DAY;\n}\n\nint main() {\n    long unixTime = 1232559922; // Example Unix Timestamp\n    double julianDate = unixToJulian(unixTime);\n\n    std::cout << "The Julian Date is: " << julianDate << std::endl;\n\n    return 0;\n}