// Include necessary headers\n   #include <emmintrin.h>  // For SSE2\n\n   extern "C" {\n       __declspec(dllexport) void AddVectorsSSE(float* vecA, float* vecB, float* result, int length) {\n           for (int i = 0; i <= length - 4; i += 4) {\n               __m128 a = _mm_loadu_ps(&vecA[i]);\n               __m128 b = _mm_loadu_ps(&vecB[i]);\n               __m128 res = _mm_add_ps(a, b);\n               _mm_storeu_ps(&result[i], res);\n           }\n       }\n   }