#include <iostream>\n#include <functional>\n\n// Define a template function that takes the number of iterations and a function object\ntemplate<typename Func>\nvoid superFor(int times, Func func) {\n    for (int i = 0; i < times; ++i) {\n        func();\n    }\n}\n\n// Example usage:\nvoid add(int a, int b) {\n    std::cout << "Sum: " << a + b << std::endl;\n}\n\nint main() {\n    // Using a lambda function\n    superFor(1, [](){\n        add(1, 2);\n    });\n\n    return 0;\n}