// Forward declaration\nclass MyClass;\n\n// This will cause a compilation error because the compiler doesn't know how to call operator&\nvoid use(MyClass* left, MyClass* right) {\n    MyClass result = *left & *right;  // Error: operator& not visible here\n}\n\n// Full definition of MyClass\nclass MyClass {\npublic:\n    MyClass operator&(const MyClass&) const { /* ... */ }\n};\n\nvoid useCorrectly() {\n    MyClass a, b;\n    MyClass c = a & b;  // Correct usage after full definition\n}