#include <windows.h>\n#include <comdef.h> // For _com_error\n#include <iostream>\n\nvoid HandleComError(HRESULT hr)\n{\n    if (FAILED(hr)) {\n        _com_error err(hr);\n        std::wcerr << L"COM Error: " << err.ErrorMessage() << std::endl;\n    }\n}\n\nint main()\n{\n    HRESULT hr = CoInitialize(NULL);\n\n    IMyComInterface* pMyComInterface = nullptr;\n    hr = CoCreateInstance(__uuidof(MyComClass), NULL, CLSCTX_INPROC_SERVER,\n                          __uuidof(IMyComInterface), (LPVOID*)&pMyComInterface);\n    HandleComError(hr); // Check for errors on creation\n\n    if (SUCCEEDED(hr))\n    {\n        hr = pMyComInterface->DoSomething();\n        HandleComError(hr); // Check for errors during method call\n\n        pMyComInterface->Release();\n    }\n\n    CoUninitialize();\n\n    return 0;\n}