#include <iostream>\n#include <vector>\n\nvoid printSubsets(int n) {\n    std::vector<int> S(n);\n    // Initialize the set S with values from 1 to n\n    for (int i = 0; i < n; ++i) {\n        S[i] = i + 1;\n    }\n\n    int totalSubsets = 1 << n; // This is 2^n\n\n    // Iterate over all possible subsets\n    for (int subsetMask = 0; subsetMask < totalSubsets; ++subsetMask) {\n        std::cout << "{ ";\n        for (int i = 0; i < n; ++i) {\n            // Check if the i-th element should be included in this subset\n            if (subsetMask & (1 << i)) {\n                std::cout << S[i] << " ";\n            }\n        }\n        std::cout << "}" << std::endl;\n    }\n}\n\nint main() {\n    int n = 5; // Example with set {1, 2, 3, 4, 5}\n    printSubsets(n);\n    return 0;\n}