#include <iostream>\n#include <map>\n#include <string>\n#include <list>\n\nint main() {\n    typedef std::multimap<std::string, std::string> MyMultimap;\n    MyMultimap myMmap;\n\n    // Insert a new element and get an iterator to it\n    auto it = myMmap.insert(MyMultimap::value_type("foo1", "test"));\n\n    // Create a list of iterators from multimap elements\n    std::list<MyMultimap::iterator> myList;\n\n    // Add the iterator to the list\n    myList.push_back(it);\n\n    return 0;\n}