#include <QtWidgets/QApplication>\n#include <QtCharts/QChartView>\n#include <QtCharts/QLegend>\n#include <QtCharts/QLineSeries>\n#include <QtCharts/QCandlestickSeries>\n#include <QtCharts/QCandlestickSet>\n\nQT_CHARTS_USE_NAMESPACE\n\nint main(int argc, char *argv[])\n{\n    QApplication a(argc, argv);\n\n    QCandlestickSeries *acmeSeries = new QCandlestickSeries();\n    acmeSeries->setName("Acme");\n\n    QList<QCandlestickSet *> setList;\n    setList.append(new QCandlestickSet(63.2657, 65.1207, 62.6443, 64.7989));\n    setList.append(new QCandlestickSet(65.1207, 65.7954, 64.4238, 65.5059));\n    // Add more sets as needed for your chart\n\n    acmeSeries->append(setList);\n\n    QChart *chart = new QChart();\n    chart->addSeries(acmeSeries);\n    chart->setTitle("ACME Corporation");\n    chart->setAnimationOptions(QChart::SeriesAnimations);\n\n    chart->legend()->setVisible(true);\n    chart->legend()->setAlignment(Qt::AlignBottom);\n\n    QChartView *chartView = new QChartView(chart);\n    chartView->setRenderHint(QPainter::Antialiasing);\n\n    chartView->resize(1024, 768);\n    chartView->show();\n\n    return a.exec();\n}