#include <cstdlib> // For system\n\nint main() {\n    const char* filePath = "example.txt"; // Replace with your file path\n    #ifdef _WIN32\n        std::string command = "start \"\" \"" + std::string(filePath) + "\"";\n    #elif __APPLE__\n        std::string command = "open \"" + std::string(filePath) + "\"";\n    #elif __linux__\n        std::string command = "xdg-open \"" + std::string(filePath) + "\"";\n    #endif\n\n    int result = system(command.c_str());\n    if (result != 0) {\n        // Handle error\n        return -1;\n    }\n    return 0;\n}