#include <windows.h>\n#include <cred_uiapi.h>\n#include <iostream>\n\nint main() {\n    CREDUI_INFO cui;\n    TCHAR pszUserName[CREDUI_MAX_USERNAME_LENGTH + 1];\n    TCHAR pszPassword[CREDUI_MAX_PASSWORD_LENGTH + 1];\n    DWORD dwAuthError;\n    BOOL fSave;\n\n    cui.cbSize = sizeof(CREDUI_INFO);\n    cui.hwndParent = NULL;\n    cui.pszMessageText = TEXT("Please enter your network credentials:");\n    cui.pszCaptionText = TEXT("Network Authentication");\n    cui.hbmBanner = NULL;\n\n    if (CredUIPromptForCredentials(\n            &cui,\n            TEXT("\\\\your-network-share"),\n            NULL,\n            0, // error code\n            pszUserName,\n            CREDUI_MAX_USERNAME_LENGTH + 1,\n            pszPassword,\n            CREDUI_MAX_PASSWORD_LENGTH + 1,\n            &fSave,\n            CREDUI_FLAGS_GENERIC_CREDENTIALS | \n            CREDUI_FLAGS_DO_NOT_PERSIST |\n            CREDUI_FLAGS_ALWAYS_SHOW_UI) == ERROR_CANCELLED)\n    {\n        std::cout << "User cancelled the credential prompt." << std::endl;\n        return 1; // Exit if user cancels\n    }\n    \n    // Use the credentials to connect to network resource or use them in your logic\n\n    std::wcout << L"Username: " << pszUserName << std::endl;\n\n    // Securely handle passwords and clean up sensitive data\n    SecureZeroMemory(pszPassword, sizeof(pszPassword));\n\n    return 0;\n}