#include <soci/soci.h>\n#include <iostream>\n\nint main() {\n    try {\n        soci::session sql(soci::oracle, "user/pass@db");\n        // For MySQL: soci::session sql(soci::mysql, "dbname=mydb user=myuser password=mypass");\n        // For SQL Server: soci::session sql(soci::odbc, "DSN=mydsn;UID=user;PWD=pass");\n\n        std::string result;\n        sql << "SELECT name FROM users WHERE id = 1", into(result);\n\n        std::cout << "Result: " << result << std::endl;\n    } catch (std::exception const &e) {\n        std::cerr << "Error: " << e.what() << '\n';\n    }\n}