#include <ace/OS_main.h>\n#include <ace/Acceptor.h>\n#include <ace/SOCK_Acceptor.h>\n#include <ace/SOCK_Stream.h>\n\nclass MyHandler : public ACE_Svc_Handler<ACE_SOCK_STREAM, ACE_NULL_SYNCH> {\npublic:\n    int open(void*) override {\n        // Handle the incoming connection...\n        return 0;\n    }\n};\n\nint ACE_TMAIN(int argc, ACE_TCHAR *argv[]) {\n    ACE_INET_Addr addr(8001);\n    ACE_Acceptor<MyHandler, ACE_SOCK_ACCEPTOR> acceptor(addr);\n    acceptor.activate();\n    for (;;) {\n        ACE_Time_Value timeout(ACE_OS::gettimeofday() + 10);\n        if (acceptor.handle_events(&timeout) == -1)\n            return -1;\n    }\n}