#include <QApplication>\n#include <QWebEngineView>\n\nint main(int argc, char *argv[]) {\n    QApplication app(argc, argv);\n\n    QWebEngineView view;\n    view.setHtml("<html><body>"\n                 "<h1>Welcome to My C++ App!</h1>"\n                 "<p>Here is an HTML GUI.</p>"\n                 "</body></html>");\n    view.resize(800, 600);\n    view.show();\n\n    return app.exec();\n}