#include <windows.h>\n#include <iostream>\n\nvoid CreateFileAndLog(const std::string& fileName) {\n    HANDLE hFile = CreateFileA(\n        fileName.c_str(),\n        GENERIC_READ | GENERIC_WRITE,\n        0,          // No sharing \n        NULL,       // Default security attributes\n        CREATE_ALWAYS,\n        FILE_ATTRIBUTE_NORMAL,\n        NULL);\n\n    if (hFile == INVALID_HANDLE_VALUE) {\n        std::cerr << "Failed to create file: " << fileName << std::endl;\n    } else {\n        std::cout << "Created file handle for: " << fileName << std::endl;\n        // Use the file handle here...\n        CloseHandle(hFile);\n    }\n}\n\nint main() {\n    CreateFileAndLog("logA.log");\n    CreateFileAndLog("logB.log");\n    return 0;\n}