#include <iostream>\n\nvoid function(float* arr) {\n    for (int i = 0; i < 4; ++i) {\n        std::cout << arr[i] << " ";\n    }\n    std::cout << std::endl;\n}\n\nint main() {\n    // Using compound literal (C++11 feature)\n    float (&arr)[4] = *(new(std::nothrow) float[4]{1, 2, 3, 4});\n    function(arr);\n    delete[] &arr; // This is not valid and should be avoided\n    return 0;\n}