// In MyClass.h\nclass MyClass {\npublic:\n    static int getMyStaticData();\n    static void setMyStaticData(int value);\nprivate:\n    static int myStaticData;\n};\n\n// In MyClass.cpp\n#include "MyClass.h"\nint MyClass::myStaticData = 0; // Definition and initialization\n\nint MyClass::getMyStaticData() {\n    return myStaticData;\n}\n\nvoid MyClass::setMyStaticData(int value) {\n    myStaticData = value;\n}