#include <malloc.h> // Required for malloc_trim\n\nvoid allocate(int size)\n{\n    int **array = new int*[size];\n    for (int c = 0; c < size; c++) {\n        array[c] = new int;\n    }\n    cout << "after allocation of " << size << endl;\n    for (int c = 0; c < size; c++) {\n        delete array[c];\n    }\n    delete [] array;\n\n    // Try to give memory back to the OS\n    malloc_trim(0);\n}