#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Obtain the device context of the screen\n    HDC hdc = GetDC(NULL);\n\n    // Use GetDeviceCaps to get the LOGPIXELSX value\n    int dpiX = GetDeviceCaps(hdc, LOGPIXELSX);\n    \n    // Release the device context\n    ReleaseDC(NULL, hdc);\n    \n    std::cout << "LOGPIXELSX (Horizontal DPI): " << dpiX << std::endl;\n    \n    return 0;\n}