#include <windows.h>\n#include <iostream>\n\nclass SharedClass {\npublic:\n    int value;\n    // Other members...\n};\n\nint main() {\n    HANDLE hMapFile = CreateFileMapping(\n        INVALID_HANDLE_VALUE,    // Use paging file\n        NULL,                    // Default security\n        PAGE_READWRITE,          // Read/Write access\n        0,                       // Size of mapping object\n        sizeof(SharedClass),     // Size of the data to map in bytes\n        "Local\\MyFileMappingObject");  // Name of mapping object\n\n    if (hMapFile == NULL) {\n        std::cerr << "Could not create file mapping object (" << GetLastError() << ").\n";\n        return 1;\n    }\n\n    SharedClass* pBuf = (SharedClass*)MapViewOfFile(hMapFile, FILE_MAP_ALL_ACCESS, 0, 0, sizeof(SharedClass));\n\n    if (pBuf == NULL) {\n        std::cerr << "Could not map view of file (" << GetLastError() << ").\n";\n        CloseHandle(hMapFile);\n        return 1;\n    }\n\n    // Use the shared memory by accessing pBuf->value and other members\n\n    UnmapViewOfFile(pBuf);\n    CloseHandle(hMapFile);\n\n    return 0;\n}