// Class definition with member functions\nclass Rectangle {\npublic:\n    Rectangle(int w, int h) : width(w), height(h) {}\n    \n    // Member function to calculate area\n    int getArea() const {\n        return width * height;\n    }\n    \nprivate:\n    int width, height;\n};\n\n// Free function that calculates the diagonal of a rectangle\n// Does not need direct access to Rectangle's private members\ndouble getDiagonal(int width, int height) {\n    return std::sqrt(width * width + height * height);\n}\n\nint main() {\n    Rectangle rect(3, 4);\n    int area = rect.getArea(); // Using member function\n    double diagonal = getDiagonal(rect.width, rect.height); // Free function, accesses public members\n    return 0;\n}