// Example of setting up a basic Bullet environment (minimal example)\n   #include <btBulletDynamicsCommon.h>\n   btDefaultCollisionConfiguration collisionConf;\n   btCollisionDispatcher dispatcher(&collisionConf);\n   btBroadphaseInterface *overlappingPairCache = new btDbvtBroadphase();\n   btSequentialImpulseConstraintSolver solver;\n   btDiscreteDynamicsWorld dynamicsWorld(&dispatcher, overlappingPairCache, &solver, &collisionConf);