#include <fcgiapp.h>\n#include <iostream>\n\nint main() {\n    int rc = FCGX_Init();\n    if (rc == -1) return 1;\n\n    FCGX_Request request;\n    rc = FCGX_InitRequest(&request, 0, 0);\n    if (rc != 0) return 1;\n\n    for (;;) {\n        rc = FCGX_Accept_r(&request);\n        if (rc < 0) break;\n\n        std::cout << "Content-type: text/plain\r\n"\n                  << "\r\n"\n                  << "Hello world!\n";\n\n        FCGX_Finish_r(&request);\n    }\n\n    FCGX_Free(&request, true);\n\n    return 0;\n}