#include <iostream>\n#include <thread>\n#include <chrono>\n\nvoid showProgressBar(int total) {\n    for (int i = 0; i <= total; ++i) {\n        // Calculate progress percentage\n        int percent = static_cast<int>(100.0 * i / total);\n\n        // Print the progress bar to console with carriage return\n        std::cout << "\r[";\n\n        // Fill the progress bar with '=' and '.' for remaining space\n        int completed = (percent * 50) / 100; // Assume this progress bar is 50 chars long\n        for (int j = 0; j < 50; ++j) {\n            if (j < completed) std::cout << "=";\n            else std::cout << ".";\n        }\n\n        // Print the progress percentage and flush output\n        std::cout << "] " << percent << "% " << std::flush;\n\n        // Sleep for a bit to simulate work being done\n        std::this_thread::sleep_for(std::chrono::milliseconds(100));\n    }\n    std::cout << "\n"; // Move to the next line after completion\n}\n\nint main() {\n    showProgressBar(100);\n    return 0;\n}