#include <cstdint>\n#include <map>\n\nclass CoordinateKey {\npublic:\n    int32_t x;\n    int32_t y;\n\n    // Constructor\n    constexpr CoordinateKey(int32_t x, int32_t y) : x(x), y(y) {}\n\n    // Comparison operator for std::map key functionality\n    bool operator<(const CoordinateKey& other) const {\n        if (x == other.x) return y < other.y;\n        return x < other.x;\n    }\n};