#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Allocate a new console for this app\n    AllocConsole();\n\n    // Redirect std handles to the console\n    FILE* file = nullptr;\n    freopen_s(&file, "CONOUT$", "w", stdout);\n    freopen_s(&file, "CONIN$", "r", stdin);\n\n    // Now you can use cout and cin\n    std::cout << "Hello from console!" << std::endl;\n    int inputNumber;\n    std::cout << "Enter a number: ";\n    std::cin >> inputNumber;\n\n    std::cout << "You entered: " << inputNumber << std::endl;\n\n    // Keep the console open until the user presses a key\n    system("pause");\n\n    // Free the console if you want to close it programmatically\n    FreeConsole();\n\n    return 0;\n}