#include <iostream>\n#include <podofo/podofo.h>\n\nint main() {\n    try {\n        // Create a new PDF document with the current date and time in the filename.\n        Poco::PDF::PdfWriter writer("example.pdf");\n        Poco::PDF::Painter painter(writer);\n\n        // Set the font size\n        painter.setFontName("Helvetica");\n        painter.setFontSize(12.0);\n\n        // Add some text to the PDF at specified coordinates (lower left corner of the canvas)\n        painter.drawString(50, 750, "Hello, PoDoFo!");\n\n    } catch (Poco::Exception & exc) {\n        std::cerr <<(exc.displayText()) << std::endl;\n    }\n\n    return 0;\n}