#include <iostream>\n#include <string>\n\nclass MyObject {\npublic:\n    MyObject(const std::string& name) : objectName(name) {}\n\n    void printObjectName() const {\n        std::cout << "Object Name: " << objectName << std::endl;\n    }\n\nprivate:\n    std::string objectName;\n};\n\n#define CREATE_OBJECT_WITH_NAME(type, name) type name(#name);\n\nint main() {\n    CREATE_OBJECT_WITH_NAME(MyObject, myObject);\n    myObject.printObjectName();  // Outputs: Object Name: myObject\n\n    return 0;\n}