#include <windows.h>\n#include <iostream>\n\nvoid CreatePipeServer() {\n    HANDLE hPipe = CreateNamedPipe(\n        TEXT("\\\\.\\pipe\\mynamedpipe"),\n        PIPE_ACCESS_DUPLEX,\n        PIPE_TYPE_MESSAGE | PIPE_READMODE_MESSAGE | PIPE_WAIT,\n        1,\n        0,\n        0,\n        NMPWAIT_USE_DEFAULT_wait_time,\n        NULL);\n\n    if (hPipe == INVALID_HANDLE_VALUE) {\n        std::cerr << "CreateNamedPipe failed (" << GetLastError() << ").\n";\n        return;\n    }\n\n    if (ConnectNamedPipe(hPipe, NULL)) {\n        // Data exchange would go here\n        DisconnectNamedPipe(hPipe);\n    }\n\n    CloseHandle(hPipe);\n}\n\nint main() {\n    CreatePipeServer();\n    return 0;\n}