#include <iostream>\n#include <fstream>\n#include <string>\n#include <cstdlib> // for rand()\n#include <ctime>   // for time()\n\nusing namespace std;\n\nint main() {\n    srand(time(0)); // Seed the random number generator\n\n    string start = "enc"; // Assuming this is set properly in your complete code\n    char choice;\n    int z; // Number of junk characters\n    char letter;\n    string filename;\n\n    while (start == "enc") {\n        int x = 1;\n        int y = 1;\n\n        cout << "How many garbage characters would you like between each correct character?: ";\n        cin >> z;\n        cout << endl << "Please insert the name of the document you wish to encrypt, make sure you enter the name, and the file type (ie: filename.txt): ";\n        cin.ignore(); // To clear the newline left in the stream after previous input\n        getline(cin, filename); // Using getline for spaces in filenames\n\n        ifstream infile(filename);\n        if (!infile.is_open()) {\n            cout << "Failed to open input file." << endl;\n            return 1; // Exit program with error\n        }\n\n        ofstream outfile("encrypted.txt", ios::out);\n        if (!outfile.is_open()) {\n            cout << "Failed to create output file." << endl;\n            infile.close();\n            return 1; // Exit program with error\n        }\n\n        while (infile.get(letter)) { \n            while ((x - y) != z) {\n                char junkChar = static_cast<char>(33 + rand() % 94);\n                outfile << junkChar;\n                x++;\n            }\n            outfile << letter;\n            y = 1;\n            x = 1;\n        }\n\n        infile.close();\n        outfile.close();\n\n        cout << endl << "Encryption complete...please return to directory of program, a new file named encrypted.txt will be there." << endl;\n        cout << "Do you wish to try again? Please press y then enter if yes (case sensitive).";\n        cin >> choice;\n\n        if (choice != 'y') {\n            start = ""; // Change the condition to exit loop\n        }\n    }\n\n    return 0;\n}