#include <comdef.h>\n#include <iostream>\n\nvoid HandleCOMError(HRESULT hr) {\n    if (FAILED(hr)) {\n        _com_error err = _com_error(hr);\n        std::wcout << L"Error Description: " << err.ErrorMessage() << std::endl;\n    }\n}\n\nint main() {\n    HRESULT hr = CoInitialize(nullptr);\n    HandleCOMError(hr);\n\n    // Example COM call\n    CLSID clsid;\n    hr = CLSIDFromProgID(L"NonExistent.ProgID", &clsid);\n    HandleCOMError(hr);\n\n    CoUninitialize();\n    return 0;\n}