// Serialization function in Sender DLL/EXE\nvoid serializeVector(const std::vector<int>& vec, std::ostream& os) {\n    size_t size = vec.size();\n    os.write(reinterpret_cast<char*>(&size), sizeof(size));\n    if (!vec.empty()) {\n        os.write(reinterpret_cast<const char*>(vec.data()), size * sizeof(int));\n    }\n}\n\n// Deserialization function in Receiver DLL/EXE\nvoid deserializeVector(std::vector<int>& vec, std::istream& is) {\n    size_t size;\n    is.read(reinterpret_cast<char*>(&size), sizeof(size));\n    vec.resize(size);\n    if (!vec.empty()) {\n        is.read(reinterpret_cast<char*>(vec.data()), size * sizeof(int));\n    }\n}