#include <windows.h>\n#include <oleauto.h> // for LoadTypeLib\n\nint GetComObjectVersion(const char* typeLibPath) {\n    ITypeLib* pTypeLib = nullptr;\n    HRESULT hr = LoadTypeLib(typeLibPath, &pTypeLib);\n    if (FAILED(hr)) {\n        return -1; // Return error code\n    }\n\n    TLIBATTR* pTlibAttr = nullptr;\n    if (SUCCEEDED(pTypeLib->GetLibAttr(&pTlibAttr))) {\n        int majorVersion = HIWORD(pTlibAttr->wMajorVerNum);\n        int minorVersion = LOWORD(pTlibAttr->wMinorVerNum);\n\n        wprintf(L"Version: %d.%d\n", majorVersion, minorVersion);\n\n        pTypeLib->ReleaseTLibAttr(pTlibAttr);\n    } else {\n        pTypeLib->Release();\n        return -2; // Error getting library attributes\n    }\n\n    pTypeLib->Release();\n    return 0;\n}