#include <stdexcept>\n#include <iostream>\n#include <string>\n\nclass InvalidNameException : public std::runtime_error {\npublic:\n    explicit InvalidNameException(const std::string& what) : std::runtime_error(what) {}\n};\n\nvoid processDetailedName(const std::string& name) {\n    if (name.empty()) {\n        throw InvalidNameException("Detailed error: Name cannot be empty");\n    }\n    // Proceed with processing the name\n    std::cout << "Processing detailed name: " << name << std::endl;\n}\n\nint main() {\n    try {\n        processDetailedName("");\n    } catch (const InvalidNameException& e) {\n        std::cerr << "Invalid Name Error: " << e.what() << std::endl;\n    }\n    return 0;\n}