#include <stdexcept>\n#include <iostream>\n#include <string>\n\nvoid processName(const std::string& name) {\n    if (name.empty()) {\n        throw std::invalid_argument("Name cannot be empty");\n    }\n    // Proceed with processing the name\n    std::cout << "Processing name: " << name << std::endl;\n}\n\nint main() {\n    try {\n        processName("");\n    } catch (const std::invalid_argument& e) {\n        std::cerr << "Error: " << e.what() << std::endl;\n    }\n    return 0;\n}