#include <iostream>\n    #include <fstream>\n\n    void readDictionary(const std::string &filename, BST<std::pair<int, std::string>>& bst){\n        std::ifstream infile(filename);\n        if (!infile) {\n            std::cerr << "Cannot open file!\n";\n            exit(1); // or return an error code\n        }\n        int key;\n        std::string value;\n        while (infile >> key && std::getline(infile, value)){\n            bst.insert({key, value});\n        }\n        infile.close();\n    }\n\n    int main(){\n        BST<std::pair<int, std::string>> dictionaryBST;\n\n        readDictionary("dictionary.txt", dictionaryBST);\n        \n        // Remove a specific item\n        dictionaryBST.remove({5, "example"});\n\n        // Inorder traversal\n        std::cout << "Inorder Traversal: ";\n        dictionaryBST.inorder(std::cout);\n        std::cout << "\n";\n\n        // Graph representation (useful for visualization)\n        std::cout << "Graphical Representation:\n";\n        dictionaryBST.graph(std::cout);\n\n        return 0;\n    }