#include <iostream>\n#include <vector>\n#include <string>\n\nint main() {\n    std::vector<std::string> words = {"sample", "string", "for", "this", "example"};\n    std::string result;\n\n    for (size_t i = 0; i < words.size(); ++i) {\n        result += words[i];\n        if (i < words.size() - 1) { // Add space between words, not after the last one\n            result += " ";\n        }\n    }\n\n    std::cout << result << std::endl;\n    return 0;\n}