#include <iostream>\n#include <vector>\n#include <string>\n#include <numeric> // For std::accumulate\n\nint main() {\n    std::vector<std::string> words = {"sample", "string", "for", "this", "example"};\n    \n    std::string result = std::accumulate(words.begin(), words.end(),\n                                         std::string(), // Initial value\n                                         [](std::string a, const std::string& b) -> std::string {\n                                             return a.empty() ? b : a + " " + b;\n                                         });\n\n    std::cout << result << std::endl;\n    return 0;\n}