#include <iostream>\n#include <sstream>\n\nvoid formatString() {\n    int number = 42;\n    double pi = 3.14159;\n    std::string text = "example";\n\n    std::ostringstream oss;\n    oss << "Number: " << number << ", Pi: " << pi << ", Text: " << text;\n\n    std::string formattedString = oss.str();\n    std::cout << formattedString << std::endl;\n}\n\nint main() {\n    formatString();\n    return 0;\n}