#include <iostream>\n#include <vector>\n#include <cstdlib> // for rand() & srand()\n#include <ctime>   // for time()\n\nclass Maze {\nprivate:\n    std::vector<std::vector<char>> grid;\n    int width, height;\n\npublic:\n    Maze(int w, int h) : width(w), height(h) {\n        // Initialize the grid\n        grid.resize(height, std::vector<char>(width, '#'));  // Fill with walls\n\n        // Start point\n        grid[0][0] = ' ';\n    }\n\n    void printMaze() {\n        for (const auto& row : grid) {\n            for (char cell : row) {\n                std::cout << cell;\n            }\n            std::cout << "\n";\n        }\n    }\n\n    // Placeholder for maze generation algorithm\n    void generate() {\n        // Implement your algorithm here (Eller's or Kruskal's)\n    }\n};\n\nint main() {\n    srand(time(0));  // Seed the random number generator\n\n    Maze maze(21, 21);  // Create a maze of size 21x21\n    maze.printMaze();\n\n    return 0;\n}