#include "Poco/Net/TCPServer.h"\n#include "Poco/Net/ServerSocket.h"\n// Define a RequestHandler class inheriting from TCPServerConnection\nclass MyRequestHandler : public Poco::Net::TCPServerConnection {\npublic:\n    MyRequestHandler(const StreamSocket& socket) : TCPServerConnection(socket){}\n    void run() override {\n        // Handle the incoming connection...\n    }\n};\n\nint main() {\n    Poco::Net::ServerSocket svs(8001);\n    Poco::Net::TCPServer srv(new Poco::Net::TCPServerConnectionFactoryImpl<MyRequestHandler>, svs);\n    srv.start();\n    std::cout << "Server started..." << std::endl;\n    // Keep the main thread alive\n    Poco::Thread::sleep(3600*1000);\n}