#include <windows.h>\n#include <iostream>\n\nint main() {\n    HANDLE hPipe = CreateFile(\n        TEXT("\\\\.\\pipe\\MyPipe"),\n        GENERIC_READ | GENERIC_WRITE,\n        0,\n        NULL,\n        OPEN_EXISTING,\n        SECURITY_SQOS_PRESENT | SECURITY_IMPERSONATION,\n        NULL);\n\n    if (hPipe != INVALID_HANDLE_VALUE) {\n        char buffer[128];\n        DWORD bytesWritten;\n        WriteFile(hPipe, TEXT("Hello Service"), strlen("Hello Service") + 1, &bytesWritten, nullptr);\n        \n        DWORD bytesRead;\n        ReadFile(hPipe, buffer, sizeof(buffer)-1, &bytesRead, nullptr);\n        buffer[bytesRead] = 0;\n        std::cout << "Server replied: " << buffer << std::endl;\n    }\n    CloseHandle(hPipe);\n    return 0;\n}