#include <iostream>\n#include "mysql++.h"\nusing namespace std;\nusing namespace mysqlpp;\n\nint main()\n{\n    try {\n        Connection con(false); // Create a connection object\n        if (con.connect("database_name", "server_host", "user", "password")) {\n\n            Query query = con.query();\n            query << "SELECT * FROM your_table";\n            StoreQueryResult res = query.store();\n\n            for (const Row& row : res) {\n                cout << "Column 1: " << row[0] << endl;\n            }\n        } else {\n            cerr << "Connection failed: " << con.error() << endl;\n            return 1;\n        }\n    } catch (Exception& e) {\n        cerr << "MySQL++ Exception: " << e.what() << endl;\n        return 1;\n    }\n\n    return 0;\n}