#include <windows.h>\n#include <connmgr.h>\n\nBOOL CloseAllGPRSConnections() {\n    DWORD cbConnEnum = 0;\n    if (ConnMgrConnectionEnum(&cbConnEnum, NULL) != ERROR_BUFFER_TOO_SMALL)\n        return FALSE; // No connections or API error\n\n    CONNECTION_INFO* pConnInfoArray = new CONNECTION_INFO[cbConnEnum / sizeof(CONNECTION_INFO)];\n    if (!pConnInfoArray)\n        return FALSE; // Memory allocation failed\n\n    DWORD dwResult = ConnMgrConnectionEnum(&cbConnEnum, pConnInfoArray);\n    if (dwResult == ERROR_SUCCESS) {\n        for (DWORD i = 0; i < cbConnEnum / sizeof(CONNECTION_INFO); ++i) {\n            CONNECTION_INFO& ci = pConnInfoArray[i];\n            // Assuming you want to close all GPRS connections\n            ConnMgrConnectionClose(ci.dwHandle, FALSE, NULL);\n        }\n    }\n\n    delete[] pConnInfoArray;\n    return dwResult == ERROR_SUCCESS;\n}