#include <iostream>\n#include <windows.h>\n\nint main() {\n    // Note: This is an overly simplified example and does not control fan speed.\n    // It illustrates accessing hardware memory, which can be dangerous if done incorrectly.\n    \n    HANDLE hDevice = CreateFileW(\n        L"\\\\.\\PhysicalDrive0", \n        GENERIC_READ | GENERIC_WRITE,\n        FILE_SHARE_READ | FILE_SHARE_WRITE,\n        NULL,\n        OPEN_EXISTING,\n        0,\n        NULL);\n\n    if (hDevice == INVALID_HANDLE_VALUE) {\n        std::cerr << "Failed to open device." << std::endl;\n        return 1;\n    }\n\n    // This example does not show how to access ACPI or control fans safely.\n\n    CloseHandle(hDevice);\n    return 0;\n}