// math_operations.h (part of MathLib)\n#ifndef MATH_OPERATIONS_H\n#define MATH_OPERATIONS_H\nint add(int a, int b);\nint subtract(int a, int b);\n#endif\n\n// math_operations.cpp (source for MathLib)\n#include "math_operations.h"\nint add(int a, int b) { return a + b; }\nint subtract(int a, int b) { return a - b; }\n\n// string_utils.h (part of StringLib)\n#ifndef STRING_UTILS_H\n#define STRING_UTILS_H\nstd::string concatenate(const std::string& str1, const std::string& str2);\n#endif\n\n// string_utils.cpp (source for StringLib)\n#include "string_utils.h"\nstd::string concatenate(const std::string& str1, const std::string& str2) {\n    return str1 + str2;\n}\n\n// main_project.cpp using MathLib and StringLib\n#include <iostream>\n#include "math_operations.h"\n#include "string_utils.h"\n\nint main() {\n    int result = add(5, 3);\n    std::cout << "Result of addition: " << result << std::endl;\n\n    std::string combinedStr = concatenate("Hello", "World");\n    std::cout << "Concatenated String: " << combinedStr << std::endl;\n    \n    return 0;\n}