#include <windows.h>\n#include <iostream>\n\n// Function to create a console\nvoid CreateConsoleWindow() {\n    AllocConsole();\n    FILE* stream;\n    freopen_s(&stream, "CONIN$", "r", stdin);\n    freopen_s(&stream, "CONOUT$", "w", stdout);\n    freopen_s(&stream, "CONOUT$", "w", stderr);\n}\n\n// Function to close the console\nvoid CloseConsoleWindow() {\n    FreeConsole();\n}\n\nint main() {\n    // Create a console window\n    CreateConsoleWindow();\n\n    std::cout << "This is output in the console." << std::endl;\n\n    // Simulate doing some work\n    Sleep(2000);\n\n    // Optionally close the console window after work is done or at any other point\n    // CloseConsoleWindow();\n\n    // Normally you would run your main program logic here, e.g., an SFML window loop\n\n    return 0;\n}