#include <iostream>\n#include <string>\n#include <optional>\n\nvoid foo(const std::optional<std::string>& input) {\n    if (input) {\n        std::cout << input->size() << " : " << *input << std::endl;\n    } else {\n        std::cout << "NULL" << std::endl;\n    }\n}\n\nvoid bar() {\n    foo("stackoverflow");\n    foo(std::nullopt);  // Properly represents a NULL or missing value\n}